const { join  } = require( "path" ),
      fs = require( "fs" ),
      SwapAbstract = require( "./Abstract" );

class SwapLinux extends SwapAbstract {

  constructor( options ){
    super( options );
  }

  extractScript( homeDir )
  {
    let content = this.getScriptContent() +
    `echo "rm -r"
     rm -r  "\${APP_PATH}/."
     echo " "
     echo "rsync -a\${VERBOSE} --delete \${UPDATE_PATH}/. \${APP_PATH}/"
     rsync -a\${VERBOSE} --delete "\${UPDATE_PATH}/." "\${APP_PATH}/"
     ` +
    `echo " "
    echo "$APP_PATH/\${RUNNER}&"
    "$APP_PATH/\${RUNNER}"&
    echo "$APP_PATH/\${RUNNER}  "
    `,
        scriptPath = join( homeDir, "swap.sh" );
    fs.writeFileSync( scriptPath, content, "utf8" );
    fs.chmod( scriptPath, '511' ); // 511 //755
    this.scriptPath = scriptPath;
  }

}

module.exports = SwapLinux;
